import React, { useState, useEffect } from 'react';
import './EndGame.css';

export const EndGame = () => {
  const [clicked, setClicked] = useState(false);

  const handleClick = () => {
    if (!clicked) {
      setClicked(true);
      setTimeout(() => {
        setClicked(false);
      }, 2000);
    }
  };

  useEffect(() => {
    const tipButton = document.querySelector('.tip-button');
    const coin = tipButton.querySelector('.coin');

    const flipCoinLoop = () => {
      coin.moveLoopCount = 0;
      coin.maxMoveLoopCount = 90;
      coin.sideRotationCount = Math.floor(Math.random() * 5) * 90;
      coin.maxFlipAngle = (Math.floor(Math.random() * 4) + 3) * Math.PI;

      const animateCoin = () => {
        coin.moveLoopCount++;
        const percentageCompleted = coin.moveLoopCount / coin.maxMoveLoopCount;
        coin.angle = -coin.maxFlipAngle * Math.pow((percentageCompleted - 1), 2) + coin.maxFlipAngle;

        // Aquí implementas la lógica de animación para mover la moneda
        // Ajusta las propiedades CSS según sea necesario

        if (coin.moveLoopCount < coin.maxMoveLoopCount) {
          if (coin.moveLoopCount === coin.maxMoveLoopCount - 6) tipButton.classList.add('shrink-landing');
          window.requestAnimationFrame(animateCoin);
        } else {
          tipButton.classList.add('coin-landed');
          coin.style.setProperty('opacity', 0);
          setTimeout(() => {
            tipButton.classList.remove('clicked', 'shrink-landing', 'coin-landed');
            setTimeout(() => {
              resetCoin();
            }, 300);
          }, 1500);
        }
      };

      animateCoin();
    };

    const resetCoin = () => {
      // Implementa la lógica para restablecer la moneda después de la animación
    };

    tipButton.addEventListener('click', handleClick);
    return () => {
      tipButton.removeEventListener('click', handleClick);
    };
  }, []); // Asegúrate de pasar un array vacío como segundo argumento para que el efecto solo se ejecute una vez

  return (
    <div>
      <button className={`tip-button ${clicked ? 'clicked' : ''}`}>
        <span className="tip-button__text">Game over</span>
        <div className="coin-wrapper">
          <div className="coin">
            <div className="coin__middle"></div>
            <div className="coin__back"></div>
            <div className="coin__front"></div>
          </div>
        </div>
      </button>
    </div>
  );
};
