import { useState, useEffect, useRef } from "react";
import { CoinFlip } from "./CoinFlip";
import timesupImage from "../assets/images1.jpg"

export const Timer = ({ onRestart }) => {
  const initialTime = 5 ;
  const [timeRemaining, setTimeRemaining] = useState(initialTime);
  const [timeIsUp, setTimeIsUp] = useState(false);
  const [backgroundImage, setBackgroundImage] = useState(""); 
  const timerIdRef = useRef(null);
  const handleTimeUp = () => {
    setBackgroundImage(timesupImage); 
    alert(setBackgroundImage);
  };
  useEffect(() => {
    const countdown = () => {
      timerIdRef.current = setInterval(() => {
        setTimeRemaining((prevTime) => {
          if (prevTime > 0) {
            return prevTime - 1;
          } else {
            clearInterval(timerIdRef.current);
            setTimeIsUp(true); 
            handleTimeUp();
            return initialTime;
          }
        });
      }, 1000);
    };
    countdown();
    return () => clearInterval(timerIdRef.current);
  }, [onRestart, initialTime]);

  const minutes = Math.floor(timeRemaining / 60);
  const seconds = timeRemaining % 60;

  return (
    <div className="container-timer">
    <div  className="timer" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="clock"></div>
      </div>
      <p>
        {String(minutes).padStart(2, "0")}:{String(seconds).padStart(2, "0")}
      </p>
      {timeIsUp && <CoinFlip/>} 
    </div>
  );
};
